Ext.define('BMS.controller.RegisterController', {
    extend: 'Ext.app.Controller',

    onRegisterClick: function(button, e, eOpts) {
        var name, username, password, cpassword, address, mobileno, store, userObj, formpanelReg,
            formpanelLog;
        name = Ext.getCmp('rname');
        username = Ext.getCmp('rusername');
        password = Ext.getCmp('rpassword');
        cpassword = Ext.getCmp('rcpassword');
        address = Ext.getCmp('raddress');
        mobileno = Ext.getCmp('rmobileno');
        store = Ext.StoreManager.get('UserStore');
        userObj = [{
            name : name.getValue(),
            username : username.getValue(),
            password : password.getValue(),
            address : address.getValue(),
            mobileno : mobileno.getValue()
        }
                  ];
        formpanelReg = Ext.getCmp('frmReg');
        formpanelLog = Ext.getCmp('frmLogin');
        if(formpanelReg.isValid()){
        //validation of passwords
            if (password.getValue() == cpassword.getValue()){
                store.add(userObj);
                console.log(store);
                formpanelReg.setVisible(false);
                formpanelLog.setVisible(true);
            }else{
                password.markInvalid('Passwords do not match');
                cpassword.markInvalid('Passwords do not match');
            }
        }else{
            Ext.Msg.alert('Warning', 'Input required fields');
        }
    },

    init: function(application) {
        this.control({
            "#rbtnReg": {
                click: this.onRegisterClick
            }
        });
    }

});
